    $("#user_master_form_add").submit(function (e) {

        e.preventDefault();
        proceed = true;


        var ut_id = $("#ut_id").val();
        var dp_id = $("#dp_id").val();
        var um_name = $("#um_name").val();
        var um_email = $("#um_email").val();
        var um_contact1 = $("#um_contact1").val();

        if (ut_id == "") {
            alert("Please select User type.");
            $('#ut_id').focus();
            return false;
        } else if (dp_id == "") {
            alert("Please select Department.");
            $('#dp_id').focus();
            return false;
        } else if (um_name == "") {
            alert("Please enter the Name.");
            $('#um_name').focus();
            return false;
        } else if (um_email == "") {
            alert("Please enter the Username.");
            $('#um_email').focus();
            return false;
        } else if (um_contact1 == "") {
            alert("Please enter the Contact No 1.");
            $('#um_contact1').focus();
            return false;
        } else {

        }


        var post_url = $(this).attr("action");
        var request_method = $(this).attr("method");
        var form_data = new FormData(this);

        $('#submit_button').attr('disabled', 'disabled');
        $('.err_register').show();

        $('.hdn-contact-error').hide();

        $.ajax({ //ajax form submit
            url: post_url,
            type: request_method,
            data: form_data,
            dataType: "text",
            contentType: false,
            cache: false,
            processData: false
        }).done(function (result) {



            if (result.trim() == 2) {

                $('.hdn-contact-error').show();
                $('#submit_button').removeAttr('disabled');

            } else {

                $('.hdn-contact-error').hide();
                $("#practicemodal").modal("show");
                $('#submit_button').removeAttr('disabled');
            }



        });



    });

